service on aiListener {
    private final ai:Agent supportAgent;

    function init() returns error? { 
        self.supportAgent = check new (
            systemPrompt = {role: string ``, instructions: string ``}, model = supportModel, tools = []
        );
    }

    resource function post chat(@http:Payload ai:ChatReqMessage request) returns ai:ChatRespMessage|error {
        string stringResult = check self.supportAgent.run(request.message, request.sessionId);
        return {message: stringResult};
    }
}
