# A character from the Star Wars universe
public type Character distinct service object {
    # The unique identifier of the character
    resource function get id() returns string|error;
    # The name of the character
    resource function get name() returns string|error;
    # This character's friends, or an empty list if they have none
    resource function get friends() returns Character[]|error;
    # The episodes this character appears in
    resource function get appearsIn() returns Episode[]|error;
};

public enum Episode {
    JEDI,
    EMPIRE,
    NEWHOPE
}

